import React, { useEffect, useState } from 'react'
import Layout from '../../layouts/Layout'
import { Container, Card, Row, Form } from 'react-bootstrap';
import AnimeCard from '../../components/AnimeCard';
import { getAnime, getAnimeGenres } from '../../services/api.service';

const Anime: React.FC = () => {



    const [animeData, setAnimeData] = useState<{ [key: string]: any }>({});
    const [animeGenres, setAnimeGenres] = useState<{ [key: string]: any }>({});
    const [sortGenres, setSortGenres] = useState<string>('Action');

    const handelGenresCategory = (e: React.FormEvent<HTMLInputElement>): void => {

        setSortGenres(e);
    }
    useEffect(() => {
        getAnime(sortGenres).then((response) => {
            setAnimeData(response.data.data)
        });
        getAnimeGenres().then((response) => {
            setAnimeGenres(response.data.data)
        });
    }, [sortGenres]);




    return (
        <>
            <Layout title='Anime List'>
                <section className="section">

                    <div className='container-fluid'>
                        <div className="row justify-content-center">
                            <div className="col-8 filters-group-wrap">
                                <div className="filters-group">
                                    <ul className="container-filter list-inline mb-0 filter-options text-center">
                                     <li className="list-inline-item categories-name border text-dark rounded active" data-group="all">All</li>
                                        {
                                            animeGenres.length > 0 && animeGenres.map(genres => (
                                                <li className="list-inline-item categories-name border text-dark rounded" key={genres.attributes.name} onClick={() => handelGenresCategory(genres.attributes.name)}>{genres.attributes.name}</li>
                                            ))
                                        }

                                    </ul>
                                </div>
                            </div>
                            <div className="col-4">
                                <select><option>select</option></select>
                            </div>
                        </div>

                    </div>

                    <div className='container-fluid'>
                        <div id="grid" className="row mt-4 pt-2">
                            <Row>
                                {
                                    animeData.length > 0 && animeData.map(ani => (
                                        <AnimeCard key={ani.id} ani={ani} />
                                    ))
                                }

                                <div className="col-12">
                                    <ul className="pagination justify-content-center mb-0">
                                        <li className="page-item"><a className="page-link" href="#" aria-label="Previous">Prev</a></li>
                                        <li className="page-item active"><a className="page-link" href="#">1</a></li>
                                        <li className="page-item"><a className="page-link" href="#">3</a></li>
                                        <li className="page-item"><a className="page-link" href="#" aria-label="Next">Next</a></li>
                                    </ul>
                                </div>

                            </Row>



                        </div>
                    </div>
                </section>


            </Layout>

        </>
    )
}

export default Anime
