import React, { useState } from 'react'
import StepWizard from "react-step-wizard";
import Stepper from 'react-stepper-horizontal';

function UserDetails() {
    return <h2>User details</h2>;
}

function Payment() {
    return <h2>Payment information</h2>;
}

function Confirmation() {
    return <h2>Booking is confirmed</h2>;
}


const AddStepWizard: React.FC = () => {

    const [activeStep, setActiveStep] = useState(0);

    const steps = [
        { title: 'User details' },
        { title: 'Payment' },
        { title: 'Booking confirmation' },
    ];

    function getSectionComponent() {
        switch (activeStep) {
            case 0: return <UserDetails />;
            case 1: return <Payment />;
            case 2: return <Confirmation />;
            default: return null;
        }
    }

    return (
        <>
            <div>
                <Stepper
                    steps={steps}
                    activeStep={activeStep} />
                <div style={{ padding: '20px' }}>
                    {
                        getSectionComponent()
                    }

                    {
                        (activeStep !== 0 && activeStep !== steps.length - 1)
                        && <button onClick={() => setActiveStep(activeStep - 1)}>Previous</button>
                    }
                    {
                        activeStep !== steps.length - 1
                        && <button onClick={() => setActiveStep(activeStep + 1)}>Next</button>
                    }
                    {
                        activeStep == steps.length - 1
                        && <button onClick={() => setActiveStep(activeStep + 1)}>Finish</button>
                    }

                </div>
            </div>
        </>
    )
}

export default AddStepWizard
